jQuery(document).ready(function(){jQuery(".post-like a.like_button").click(function(){heart=jQuery(this); post_id=heart.data("post_id"); jQuery.ajax({type:"post",url:ajax_var.url,data:"action=post-like&nonce="+ajax_var.nonce+"&post_like=&post_id="+post_id,success:function(count){console.log(count); if(count!="already")
{heart.addClass("liked");heart.siblings(".like_count").text(count);}}});return false;});jQuery(".post-like a.dislike_button").click(function(){heart=jQuery(this); post_id=heart.data("post_id"); jQuery.ajax({type:"post",url:ajax_var.url,data:"action=post-like&nonce="+ajax_var.nonce+"&post_dislike=&post_id="+post_id,success:function(count){console.log(count); if(count!="already")
{heart.addClass("disliked");heart.siblings(".dislike_count").text(count);}}});return false;});});